// is not html but jsx(javascript extension)
import Header from "./components/Header";
import Tasks from "./components/Tasks";
import AddTask from "./components/AddTask";
import React from "react";
import { useState } from "react";

function App() {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      text: "Doc",
      day: "9.10.2010",
      reminder: true,
    },
    {
      id: 2,
      text: "Dog",
      day: "9.10.2010",
      reminder: true,
    },
    {
      id: 3,
      text: "Mom",
      day: "9.10.2010",
      reminder: false,
    },
  ]);

  const addTask = (task) => {
    const id = Math.floor(Math.random * 10000) + 1;
    const newTask = { id, ...task }; // ...task return the text, day, and reminder of the task passed as arg
    setTasks([...tasks, newTask]); // sets tasks to an array with old tasks and new task
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id != id));
  };

  //Reminder

  const toggleReminder = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id == id ? { ...task, reminder: !task.reminder } : task
      )
    );
  };
  return (
    <div className="container">
      <Header title="Task Manager"></Header>
      <AddTask onAdd={addTask} />
      {tasks.length > 0 ? (
        <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder} />
      ) : (
        "No Tasks"
      )}
    </div>
  );
}

export default App;
