import Task from "./Task";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";

const Tasks = ({
  tasks,
  onDelete,
  onToggle,
  onComplete,
  showApp,
  showShort,
  showLong,
  //showApp,
}) => {
  const shortTasks = [];
  const appointments = [];
  const longTasks = [];
  for (let i = 0; i < tasks.length; i++) {
    switch (tasks[i].type) {
      case "short":
        shortTasks.push(tasks[i]);
        break;
      case "appointment":
        shortTasks.push(tasks[i]);
        break;
      case "long":
        shortTasks.push(tasks[i]);
        break;
    }
  }
  return (
    <>
      <h2 onClick={onShow} style={{ background: "red" }}>
        Urgent Tasks
        {showShort ? <FaArrowUp /> : <FaArrowDown />}
      </h2>
      {shortTasks.map(
        (task) =>
          task.completion ===
          "deleted"(
            <Task
              key={task.id}
              task={task}
              onDelete={onDelete}
              onToggle={onToggle}
              onComplete={onComplete}
              show
            ></Task>
          )
      )}
      <h2 onClick={onShow} style={{ background: "pink" }}>
        Less Urgent Tasks
        {showLong ? <FaArrowUp /> : <FaArrowDown />}
      </h2>
      {longTasks.map(
        (task) =>
          task.completion === "deleted" && (
            <Task
              key={task.id}
              task={task}
              onDelete={onDelete}
              onToggle={onToggle}
              onComplete={onComplete}
            ></Task>
          )
      )}
      <h2 onClick={onShow} style={{ background: "aquamarine" }}>
        Appointments
        {showApp ? <FaArrowUp /> : <FaArrowDown />}
      </h2>
      {appointments.map(
        (task) =>
          task.completion === "deleted" && (
            <Task
              key={task.id}
              task={task}
              onDelete={onDelete}
              onToggle={onToggle}
              onComplete={onComplete}
            ></Task>
          )
      )}
    </>
  );
};

export default Tasks;
