import Task from "./Task";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
const ShortTasks = ({ tasks, onDelete, onToggle, showShort, onShow }) => {
  return (
    <>
      <h2>
        Immediate Tasks
        {onShow ? (
          <FaArrowUp onClick={onShow} />
        ) : (
          <FaArrowDown onClick={onShow} />
        )}
      </h2>
      {tasks.map(
        (task) =>
          task.type === "short" &&
          showShort && (
            <Task
              key={task.id}
              task={task}
              onDelete={onDelete}
              onToggle={onToggle}
              onshow={onShow}
            ></Task>
          )
      )}
    </>
  );
};

export default ShortTasks;
