import Task from "./Task";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
const Tasks = ({
  tasks,
  onDelete,
  onToggle,
  showShort,
  onShow,
  onComplete,
  type,
}) => {
  let color = "";
  let title = "";
  tasks = tasks.filter(
    (task) => task.type === type && task.completion === "in progress"
  );
  switch (type) {
    case "long":
      color = "pink";
      title = "Less Urgent Tasks";
      break;
    case "short":
      color = "red";
      title = "Urgent Tasks";
      break;
    case "appointment":
      color = "aquamarine";
      title = "Appointments";
      break;
    default:
      break;
  }
  return (
    <>
      <h2 onClick={onShow} style={{ background: color }}>
        {title}
        {showShort ? <FaArrowUp /> : <FaArrowDown />}
      </h2>

      {tasks.length > 0 ? (
        tasks.map(
          (task) =>
            showShort && (
              <Task
                key={task.id}
                task={task}
                onDelete={onDelete}
                onToggle={onToggle}
                onshow={onShow}
                onComplete={onComplete}
              ></Task>
            )
        )
      ) : (
        <h3>"No Tasks"</h3>
      )}
    </>
  );
};

export default Tasks;
