import Task from "./Task";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
const Tasks = ({
  tasks,
  onDelete,
  onToggle,
  showShort,
  onShow,
  onComplete,
  type,
}) => {
  let color = "";
  let title = "";
  switch (type) {
    case "long":
      color = "pink";
      title = "Less Urgent Tasks";
      break;
    case "short":
      color = "red";
      title = "Urgent Tasks";
      break;
    case "appointment":
      color = "aquamarine";
      title = "Appointments";
      break;
    default:
      break;
  }
  return (
    <>
      <h2 onClick={onShow} style={{ background: color }}>
        {title}
        {showShort ? <FaArrowUp /> : <FaArrowDown />}
      </h2>
      {tasks.map(
        (task) =>
          task.type === type &&
          task.completion === "in progress" &&
          showShort && (
            <Task
              key={task.id}
              task={task}
              onDelete={onDelete}
              onToggle={onToggle}
              onshow={onShow}
              onComplete={onComplete}
            ></Task>
          )
      )}
    </>
  );
};

export default Tasks;
