import Task from "./Task";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const Tasks = ({
  tasks,
  onDelete,
  onToggle,
  showShort,
  onShow,
  onComplete,
  type,
}) => {
  let color = "";
  let title = "";
  let textColor = "black";
  tasks = tasks.filter(
    (task) => task.type === type && task.completion === "in progress"
  );
  switch (type) {
    case "long":
      color = "pink";
      title = "Less Urgent Tasks";
      break;
    case "short":
      color = "red";
      title = "Urgent Tasks";
      break;
    case "appointment":
      color = "aquamarine";
      title = "Appointments";
      break;
    default:
      color = "beige";
      title = type;
    // textColor = "white";
  }
  return (
    <DragDropContext>
      <Droppable droppableId="task">
        {(provided) => (
          <div {...provided.droppableProps} ref={provided.innerRef}>
            <h2
              onClick={onShow}
              style={{ background: color, color: textColor }}
            >
              {title}
              {showShort ? <FaArrowUp /> : <FaArrowDown />}
            </h2>

            {tasks.length > 0
              ? tasks.map(
                  (task, index) =>
                    showShort && (
                      <Draggable key={task.id} draggableId={task.id}>
                        {" "}
                        index = {index}
                        {(provided) => (
                          <li
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            <Task
                              key={task.id}
                              task={task}
                              onDelete={onDelete}
                              onToggle={onToggle}
                              onComplete={onComplete}
                            ></Task>
                          </li>
                        )}
                      </Draggable>
                    )
                )
              : showShort && <h3 style={{ marginLeft: "30px" }}>No Tasks</h3>}
          </div>
        )}
      </Droppable>
    </DragDropContext>
  );
};

export default Tasks;
