import Task from "./Task";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
const ShortTasks = ({ tasks, onDelete, onToggle, showShort, onShow }) => {
  return (
    <>
      <h2 onClick={onShow} style={{ background: "red" }}>
        Urgent Tasks
        {showShort ? <FaArrowUp /> : <FaArrowDown />}
      </h2>
      {tasks.map(
        (task) =>
          task.type === "short" &&
          task.completion === "in progress" &&
          showShort && (
            <Task
              key={task.id}
              task={task}
              onDelete={onDelete}
              onToggle={onToggle}
              onshow={onShow}
            ></Task>
          )
      )}
    </>
  );
};

export default ShortTasks;
