// is not html but jsx(javascript extension)
import Header from "./components/Header";
import Tasks from "./components/Tasks";
import AddTask from "./components/AddTask";
import React from "react";
import { useState, useEffect } from "react";
import ShortTasks from "./components/ShortTasks";
import LongTasks from "./components/LongTasks";
import Appointments from "./components/Appointments";

const App = () => {
  const [showAddTask, setShowAddTask] = useState(false);
  const [showShortTasks, setShowShortTasks] = useState(true);
  const [showLongTasks, setShowLongTasks] = useState(true);
  const [showAppointments, setShowAppointments] = useState(true);

  const [tasks, setTasks] = useState([]);
  useEffect(() => {
    const getTasks = async () => {
      const tasksFromServer = await fetchTasks();
      setTasks(tasksFromServer);
    };
    getTasks();
  }, []); // dependancy array

  const fetchTasks = async () => {
    const res = await fetch("http://localhost:7000/tasks");
    const data = await res.json();
    return data;
  };
  const fetchTask = async (id) => {
    const res = await fetch(`http://localhost:7000/tasks/${id}`);
    const data = await res.json();
    return data;
  };

  // const addTask = (task) => {
  //   const id = Math.floor(Math.random() * 10000) + 1;
  //   console.log(id);
  //   const newTask = { id, ...task }; // ...task return the text, day, and reminder of the task passed as arg
  //   setTasks([...tasks, newTask]); // sets tasks to an array with old tasks and new task
  // };

  const addTask = async (task) => {
    const res = await fetch("http://localhost:7000/tasks", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(task),
    });
    const data = await res.json();
    setTasks([...tasks, data]); // sets tasks to an array with old tasks and new task
  };

  // const deleteTask = (id) => {
  //   setTasks(tasks.filter((task) => task.id !== id));
  // };
  const deleteTask = async (id) => {
    await fetch(`http://localhost:7000/tasks/${id}`, {
      method: "DELETE",
    });
    setTasks(tasks.filter((task) => task.id !== id));
  };

  //Reminder

  const toggleReminder = async (id) => {
    const taskToToggle = await fetchTask(id);
    const updTask = { ...taskToToggle, reminder: !taskToToggle.reminder };
    const res = await fetch(`http://localhost:7000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(updTask),
    });
    const data = await res.json();

    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, reminder: data.reminder } : task
      )
    );
  };
  return (
    <div className="container">
      <Header
        onAdd={() => setShowAddTask(!showAddTask)}
        showAdd={showAddTask}
      />
      {showAddTask && <AddTask onAdd={addTask} />}
      {/* {tasks.length > 0 ? (
        <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder} />
      ) : (
        "No Tasks"
      )} */}
      <ShortTasks
        tasks={tasks}
        onDelete={deleteTask}
        onToggle={toggleReminder}
        onShow={() => setShowShortTasks(!showShortTasks)}
        showShort={showShortTasks}
      />
      <LongTasks
        tasks={tasks}
        onDelete={deleteTask}
        onToggle={toggleReminder}
        onShow={() => setShowLongTasks(!showLongTasks)}
        showLong={showLongTasks}
      />
      <Appointments
        tasks={tasks}
        onDelete={deleteTask}
        onToggle={toggleReminder}
        onShow={() => setShowAppointments(!showAppointments)}
        showApp={showAppointments}
      />
    </div>
  );
};

export default App;
